<script setup lang='ts'>

const target = {
  name: 'test',
  latitude: 35.160064,
  longitude: 136.9341952
}

let video: HTMLVideoElement
let canvas: HTMLCanvasElement
let question: HTMLImageElement
let pos: {
  frontToBack: number;
  leftToRight: number;
  rotateDegrees: number;
}

onMounted(() => {
  if (typeof window === 'object') {
    video = document.getElementsByTagName('video')[0];
    canvas = document.getElementsByTagName('canvas')[0];
    const ctx = canvas.getContext('2d');
    if (ctx !== null) {
      question = new Image();
      question.src = '/_nuxt/assets/images/question.png'
      question.onload = () => {
        updateCanvas(ctx, video, canvas);
      }
    }

    if (window.DeviceOrientationEvent) {
        window.addEventListener('deviceorientation', (event) => {
            handleOrientationEvent(event.alpha, event.gamma, event.beta);
        }, true);
    }
    else {
      console.log('window.DeviceOrientationEvent: false')
    }

    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      navigator.mediaDevices.getUserMedia({ audio: false, video: true }).then((stream) => {
        video.srcObject = stream;
        video.play();
      });
    }

    if (navigator.geolocation && navigator.geolocation.watchPosition) {
      navigator.geolocation.watchPosition((position) => {
        const latitude = position.coords.latitude;
        const longitude = position.coords.longitude;
        console.log(latitude, longitude);
        const distance = getDistance(target.latitude, target.latitude, latitude, longitude)
        if (distance < 10) {
          beep(10, 4)
        }
        else if (distance < 100) {
          beep(20, 4)
        }
        else {
          beep(60, 4)
        }
      });
    }
  }
});

const updateCanvas = (
  ctx: CanvasRenderingContext2D,
  video: HTMLVideoElement,
  canvas: HTMLCanvasElement
) => {
  ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
  ctx.drawImage(question, (canvas.width - 50) / 2, (canvas.height - 50) / 2, 50, 50)
  const id =  requestAnimationFrame(updateCanvas.bind(null, ctx, video, canvas));
}

const handleOrientationEvent = (frontToBack: number | null, leftToRight: number | null, rotateDegrees: number | null) => {
  pos.frontToBack = frontToBack === null ? 0 : frontToBack
  pos.leftToRight = leftToRight === null ? 0 : leftToRight
  pos.rotateDegrees = rotateDegrees === null ? 0 : rotateDegrees
};

const R = Math.PI / 180;

const getDistance = (lat1: number, lng1: number, lat2: number, lng2: number) => {
  lat1 *= R;
  lng1 *= R;
  lat2 *= R;
  lng2 *= R;
  return 6371 * Math.acos(Math.cos(lat1) * Math.cos(lat2) * Math.cos(lng2 - lng1) + Math.sin(lat1) * Math.sin(lat2));
}

const beep = (tempo: number, beat: number) => {
  
  const low = new Audio('data:audio/wav;base64,')
  const high = new Audio('data:audio/wav;base64,')

   setInterval(() => {
    low.play();
  }, 60 / tempo * 1000);

  setInterval(() => {
    high.play();
  }, 60 / tempo * 1000 * beat);
}
</script>

<template>
  <div>
    <video></video>
    <canvas></canvas>
  </div>
</template>

<style scoped>
div {
  margin: 0;
  padding: 0;
  height: 100%;
  width: 100%;
}

video {
  display: none;
}

canvas {
  margin: 0;
  padding: 0;
  height: 100%;
  width: 100%;
}
</style>
