<script setup lang="ts">
var beepLow: any;
var beepHigh: any;
var volume = 0.25;

const target = {
  latitude: 35.160064,
  longitude: 136.9341952,
};

const pos = reactive({
  latitude: 0.0,
  longitude: 0.0,
  distance: 0.0,
});

onMounted(() => {
  if (navigator.geolocation && navigator.geolocation.watchPosition) {
    navigator.geolocation.watchPosition((position) => {
      pos.latitude = position.coords.latitude;
      pos.longitude = position.coords.longitude;

      chkDistance(
        target.latitude,
        target.longitude,
        pos.latitude,
        pos.longitude
      );
    });
  }
});

const chkDistance = (
  targetLatitude: number,
  targetLongitude: number,
  parentLatitude: number,
  parentLongitude: number
) => {
  pos.distance = getDistance(
    targetLatitude,
    targetLongitude,
    parentLatitude,
    parentLongitude
  );

  clearInterval(beepLow);
  clearInterval(beepHigh);

  if (pos.distance < 1) {
    beep(60, 0.5);
  } else if (pos.distance < 3) {
    beep(20, 0.5);
  } else if (pos.distance < 5) {
    beep(20, 0.5);
  } else if (pos.distance < 8) {
    beep(20, 0.5);
  } else if (pos.distance < 10) {
    beep(30, 0.5);
  } else {
    beep(360, 0.5);
  }
};

const getDistance = (
  lat1: number,
  lng1: number,
  lat2: number,
  lng2: number
) => {
  lat1 *= Math.PI / 180;
  lng1 *= Math.PI / 180;
  lat2 *= Math.PI / 180;
  lng2 *= Math.PI / 180;
  return (
    6371 *
    Math.acos(
      Math.cos(lat1) * Math.cos(lat2) * Math.cos(lng2 - lng1) +
        Math.sin(lat1) * Math.sin(lat2)
    )
  );
};

const beep = (tempo: number, beat: number) => {
  const low = new Audio(
    "data:audio/wav;base64,"
  );
  const high = new Audio(
    "data:audio/wav;base64,"
  );

  low.volume = volume;
  high.volume = volume;

  beepLow = setInterval(() => {
    low.play();
  }, (60 / tempo) * 1000);

  beepHigh = setInterval(() => {
    // high.play();
  }, (60 / tempo) * 1000 * beat);
};
</script>

<template>
  <div class="container">
    --- GPS ---
    <p>{{ pos }}</p>
  </div>
</template>

